mapper.select:
select *
from request_form
where request_nickname = #{nickname}
and done_flag = #{done_flag} 
order by rid desc

select *
from request_form
where response_nickname = #{nickname} 
and done_flag = #{done_flag}
order by rid desc

mapper.update:
update request_form set
		done_flag = 2
where rid = #{rid}

update request_form set
		done_flag = 3
where rid = #{rid}

update notify set
	read_flag = 1
where nid = #{nid}

update notify set
	read_flag = 1
where response_nickname = #{response_nickname}

update request_form set
		done_flag = 4
where rid = #{rid}

update request_form set
		request_nickname = #{nickname}
where request_nickname = #{orgNickname}

update request_form set
		response_nickname = #{nickname}
where response_nickname = #{orgNickname}

update notify set
		request_nickname = #{nickname}
where request_nickname = #{orgNickname}

update notify set
	response_nickname = #{nickname}
where response_nickname = #{orgNickname}
